import React from 'react'
import {View, Text, TouchableWithoutFeedback, ImageBackground, StyleSheet, Dimensions} from 'react-native'
import {DARK_SHADE} from "../../theme";

export const CategoryItem = ({title, image, length, totalLength}) => {

    const windowHeight = Dimensions.get('window').height
    const windowWidth = Dimensions.get('window').width

    let finalHeight
        if(totalLength === 2) {
            finalHeight = windowHeight/2
        }
        else {
            finalHeight = windowHeight
        }

    console.log('IMAGE', image, totalLength)
    return(
        <TouchableWithoutFeedback >
            <ImageBackground source={image} style={[styles.image, {width:windowWidth, height: finalHeight}]}>
                <View style={styles.description}>
                    <Text style={styles.length}>{length}</Text>
                    <Text style={styles.title}>{title}</Text>
                </View>
            </ImageBackground>
        </TouchableWithoutFeedback>
    )
}

const styles = StyleSheet.create({
    image:{
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center'
    },
    description: {
        flex: 1,
        width: '100%',
        backgroundColor: DARK_SHADE
    },
    length: {
        color: 'white',
        textAlign: 'center',
        fontWeight: 'bold',
        fontSize: 24
    },
    title: {
        color: 'white',
        textAlign: 'center',
        fontSize: 16
    }
})
