import {ADD_ITEM, SET_ADD_ITEM_MODAL} from "../types";
import {UUID} from "../data/UUID";

export const setAddItemModal = (payload) => {
    return{
        type: SET_ADD_ITEM_MODAL,
        payload
    }
}

export const addItem = (item) => {
    const id = UUID().toString();
    item.id = id
    if (item.category === "") {
        item.category = "uncategorized"
    }
    return{
        type: ADD_ITEM,
        item
    }
}
