import {categoriesData} from "../data/categoriesData";
import {ADD_ITEM} from "../types";

export const initialState = categoriesData

export const categoriesReducer = (state = initialState, action) => {
    switch(action.type) {
        case ADD_ITEM:
            // const category = action.item.category
            const category = state.categories.find(p => p.id === action.item.category)
            return {
                ...state,
                categories: state.items.map(item => {
                    if (item.id === action.item.category) {
                        item.subcategories.map(i => {
                            if(i.id === action.item.type) {
                                i.items = [...i.items, {...action.item.id}]
                            }
                            return i
                        })
                    }
                    return item
                })
            }
        default: return state
    }
}
