import React from 'react'
import {View, Text, TouchableWithoutFeedback, ImageBackground, StyleSheet, Dimensions} from 'react-native'

export const CategoryItem = ({title, image, length, totalLength}) => {

    const windowHeight = Dimensions.get('window').height
    const windowWidth = Dimensions.get('window').width

    console.log('IMAGE', image, totalLength)
    return(
        <TouchableWithoutFeedback >
            <ImageBackground source={image} style={[styles.image, {width:windowWidth, height: windowHeight/2 }]}>
                <View style={styles.description}>
                    <Text style={styles.length}>{length}</Text>
                    <Text style={styles.title}>{title}</Text>
                </View>
            </ImageBackground>
        </TouchableWithoutFeedback>
    )
}

const styles = StyleSheet.create({
    image:{
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center'
    },
    length: {
        color: 'white',
        textAlign: 'center',
        fontWeight: 'bold',
        fontSize: 30
    },
    title: {
        color: 'white',
        textAlign: 'center'
    }
})
