const initialState = {
    items: [
        // {
        //     id: "1",
        //     brand: "ZARA",
        //     img: require('../../../assets/test/13.jpg'),
        //     color: "#000000",
        //     type: "blazers",
        //     category: "top"
        // },
        // {
        //     id: "2",
        //     brand: "Stradivarius",
        //     img: require('../../../assets/test/12.jpg'),
        //     color: "#ffffff",
        //     type: "blazers",
        //     category: "top"
        // },
        // {
        //     id: "3",
        //     brand: "Pull&Bear",
        //     img: require('../../../assets/test/11.jpg'),
        //     color: "#000000",
        //     type: "boots",
        //     category: "shoes"
        // }
    ]
}

export const itemsReducer = (state = initialState, action) => {
    switch(action.type) {
        case ADD_ITEM:
            return {
                ...state,
                items: [...state.items, {...action.item}]
            }
        default: return state
    }
}
