import {Modal, Text, View, ImageBackground, StyleSheet} from "react-native";
import React, {useEffect, useState} from "react";
import {Shade} from "./ui/Shade";
import {DoneTopButton} from "./ui/buttons/DoneTopButton";
import {useDispatch, useSelector} from "react-redux";
import {addItem, setAddItemModal} from "../store/actions";
import {TopActions} from "./ui/buttons/TopActions";

export const AddItemModal = ({img}) => {
    const dispatch = useDispatch()
    const [modalMain, setModal] = useState(false)
    const modalVisbility = useSelector(state => state.settings.addItemModal)

    useEffect(() => {
        if (modalVisbility) {
            setModal(true)
        }
        else {
            setModal(false)
        }
    }, [modalVisbility])

    const handleDone = () => {
        let item = {
            brand: "",
            img: img,
            color: "",
            type: "",
            category: ""
        }
        dispatch(setAddItemModal(false))
        dispatch(addItem(item))
    }
    return(
        <Modal
            animationType="slide"
            transparent={false}
            visible={modalMain}
        >
            <TopActions>
                <View></View>
                <DoneTopButton pressDone={handleDone}/>
            </TopActions>
            <ImageBackground source={{ uri: img }} style={styles.image}>
                 <Text>It modal</Text>
                <Shade/>
            </ImageBackground>
        </Modal>
    )
}

const styles = StyleSheet.create({
    image: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
        width: '100%',
        height: '100%'
    }
})
