import {Modal, Text, View, ImageBackground, StyleSheet} from "react-native";
import React from "react";
import {Shade} from "./ui/Shade";
import {DoneTopButton} from "./ui/buttons/DoneTopButton";
import {useDispatch, useSelector} from "react-redux";
import {setAddItemModal} from "../store/actions";

export const AddItemModal = ({img}) => {
    const dispatch = useDispatch()
    const modal = useSelector(state => state.settings)
   console.log('Modal', modal)
    return(
        <Modal
            animationType="slide"
            transparent={false}
            visible={false}
            onRequestClose={() => {
                Alert.alert("Modal has been closed.");
            }}
        >
            <DoneTopButton pressDone={dispatch(setAddItemModal(false))}/>
            <ImageBackground source={{ uri: img }} style={styles.image}>
                 <Text>It modal</Text>
                <Shade/>
            </ImageBackground>
        </Modal>
    )
}

const styles = StyleSheet.create({
    image: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
        width: '100%',
        height: '100%'
    }
})
