import {SET_ADD_ITEM_MODAL} from "../types";

const initialState = {
    itemsAdded: false,
    addItemModal: false
}

export const settingsReducer = (state = initialState, action) => {
    switch(action.type) {
        case SET_ADD_ITEM_MODAL:
            return {
                ...state,
                addItemModal: action.payload
            }
        default: return state
    }
}
