import React from 'react'
import { NavigationContainer } from '@react-navigation/native';
import {createStackNavigator} from '@react-navigation/stack';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import {LookScreen} from "../screens/LookScreen";
import {WardrobeScreen} from "../screens/WardrobeScreen";
import {View} from "react-native";
import {PINK_COLOR} from "../theme";
import i18n from 'i18n-js';

const Stack = createStackNavigator();
const Tab =  createBottomTabNavigator()

export const AppNavigation = () => {
    return(
        <NavigationContainer>
            <Tab.Navigator
                initialRouteName="Wardrobe"
                tabBarOptions={{
                    style: {
                        position: 'absolute',
                        backgroundColor: 'transparent',
                        borderTopColor: "transparent"
                    },
                    labelStyle: {
                        fontSize: 16,
                        paddingBottom: 20,
                        fontWeight: '600',
                        color: 'white'
                    }
                }}
                screenOptions={{
                    tabBarIcon: ({ focused, color, size }) => {
                        const activeStyle = focused ? PINK_COLOR : 'transparent';
                        return <View
                            style={{
                            width: 8,
                            height: 8,
                            backgroundColor: activeStyle,
                            borderRadius: 100,
                            marginBottom: 20
                        }}
                        ></View>;
                    }
                }}
            >
                <Tab.Screen name={i18n.t('look')} component={LookScreen} />
                <Tab.Screen name={i18n.t('wardrobe')} component={WardrobeScreen} />
            </Tab.Navigator>
        </NavigationContainer>
    )
}
